import React, {useEffect, useState} from 'react';
import axios from 'axios';

const Examination = () => {
    const [examinations, setExaminations] = useState([]);
    const [formData, setFormData] = useState({
        passport_id: '',
        mia_id: '',
        passed_category: '',
        is_passed: '',
    });

    useEffect(() => {
        axios
            .get('http://localhost:5000/api/examination/')
            .then((response) => {
                setExaminations(response.data);
            })
            .catch((error) => {
                console.log(error);
            });
    }, []);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        axios
            .get('http://localhost:5000/api/examination', { params: formData })
            .then((response) => {
                setExaminations(response.data);
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const handleDelete = (id) => {
        axios
            .delete(`/api/examinations/${id}`)
            .then((response) => {
                setExaminations((prevState) =>
                    prevState.filter((examination) => examination.id !== id)
                );
            })
            .catch((error) => {
                console.log(error);
            });
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="passport_id"
                    placeholder="Passport ID"
                    value={formData.passport_id}
                    onChange={handleInputChange}
                />
                <input
                    type="text"
                    name="mia_id"
                    placeholder="MIA ID"
                    value={formData.mia_id}
                    onChange={handleInputChange}
                />
                <input
                    type="text"
                    name="passed_category"
                    placeholder="Passed Category"
                    value={formData.passed_category}
                    onChange={handleInputChange}
                />
                <select name="is_passed" value={formData.is_passed} onChange={handleInputChange}>
                    <option value="">--Select Pass Status--</option>
                    <option value="true">Yes</option>
                    <option value="false">No</option>
                </select>
                <button type="submit">Search</button>
            </form>
            <table>
                <thead>
                <tr>
                    <th>Passport ID</th>
                    <th>MIA ID</th>
                    <th>Passed Category</th>
                    <th>Is Passed</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {examinations.map((examination) => (
                    <tr key={examination.id}>
                        <td>{examination.passport_id}</td>
                        <td>{examination.mia_id}</td>
                        <td>{examination.passed_category}</td>
                        <td>{examination.is_passed ? 'Yes' : 'No'}</td>
                        <td>
                            <button onClick={() => handleDelete(examination.id)}>Delete</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default Examination;
