import React, {useEffect, useState} from 'react';


const Examination = () => {
    const [examinations, setExaminations] = useState([]);

    useEffect(() => {
        fetch('/examination') // replace with the URL to your backend server endpoint
            .then((response) => response.json())
            .then((data) => setExaminations(data))
            .catch((error) => console.error(error));
    }, []);

    return (
        <div>
            <h1>Examinations</h1>
            <table>
                <thead>
                <tr>
                    <th>Passport ID</th>
                    <th>MIA ID</th>
                    <th>Passed Category</th>
                    <th>Is Passed</th>
                </tr>
                </thead>
                <tbody>
                {examinations.map((examination) => (
                    <tr key={examination.id}>
                        <td>{examination.passport_id}</td>
                        <td>{examination.mia_id}</td>
                        <td>{examination.passed_category}</td>
                        <td>{examination.is_passed ? 'Yes' : 'No'}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default Examination;
