import React, {useEffect, useState} from 'react';


const Examination = () => {
    const [examinations, setExaminations] = useState([]);
    const [formData, setFormData] = useState({
        passport_id: '',
        mia_id: '',
        passed_category: '',
        is_passed: false,
    });

    useEffect(() => {
        fetch('http://localhost:5000/api/examination/') // replace with the URL to your backend server endpoint
            .then((response) => response.json())
            .then((data) => setExaminations(data))
            .catch((error) => console.error(error));
    }, []);

    const handleSubmit = (event) => {
        event.preventDefault();

        fetch('http://localhost:5000/api/examination/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        })
            .then((response) => response.json())
            .then((data) => {
                setExaminations([...examinations, data]);
                setFormData({
                    passport_id: '',
                    mia_id: '',
                    passed_category: '',
                    is_passed: false,
                });
            })
            .catch((error) => console.error(error));
    };

    const handleDelete = (id) => {
        fetch(`/api/examinations/${id}`, {
            method: 'DELETE',
        })
            .then(() => {
                setExaminations(examinations.filter((examination) => examination.id !== id));
            })
            .catch((error) => console.error(error));
    };

    return (
        <div>
            <h1>Examinations</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Passport ID:
                    <input
                        type="text"
                        value={formData.passport_id}
                        onChange={(event) => setFormData({ ...formData, passport_id: event.target.value })}
                    />
                </label>
                <label>
                    MIA ID:
                    <input
                        type="text"
                        value={formData.mia_id}
                        onChange={(event) => setFormData({ ...formData, mia_id: event.target.value })}
                    />
                </label>
                <label>
                    Passed Category:
                    <input
                        type="text"
                        value={formData.passed_category}
                        onChange={(event) => setFormData({ ...formData, passed_category: event.target.value })}
                    />
                </label>
                <label>
                    Is Passed:
                    <input
                        type="checkbox"
                        checked={formData.is_passed}
                        onChange={(event) => setFormData({ ...formData, is_passed: event.target.checked })}
                    />
                </label>
                <button type="submit">Add Examination</button>
            </form>
            <table>
                <thead>
                <tr>
                    <th>Passport ID</th>
                    <th>MIA ID</th>
                    <th>Passed Category</th>
                    <th>Is Passed</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {examinations.map((examination) => (
                    <tr key={examination.id}>
                        <td>{examination.passport_id}</td>
                        <td>{examination.mia_id}</td>
                        <td>{examination.passed_category}</td>
                        <td>{examination.is_passed ? 'Yes' : 'No'}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default Examination;
